import React, {Component} from 'react'
import NavBar from './NavBar'
import { Redirect } from 'react-router-dom'
import {Tabs, Tab, Button, Form, Col, Row, Modal} from 'react-bootstrap'
import chess from './chess1.jpg'

class PlayWithFriends extends Component {

    state = {
        gameId: null,
        color: null,
        playerName: null,
        showInvalid : false
    }

    constructor(props) {
        super(props);
        this.joinGameId = React.createRef();
        this.joinPlayerName = React.createRef();
        this.createPlayerName = React.createRef();
    }

    getGameId = () => {
        fetch('http://localhost:8000/gameId/'+this.createPlayerName.current.value)
        .then((response) => {
            return response.json()
        })
        .then((data) => {
            this.setState({gameId: data.gameId, color: data.color, playerName: this.createPlayerName.current.value})            
        })
        .catch(error => {})
    }

    joinGame = () => {
        fetch('http://localhost:8000/validate?gameId='+this.joinGameId.current.value+'&userId='+this.joinPlayerName.current.value)
        .then((response) => {
            return response.json()
        })
        .then((data) => {
            if(data.valid){
                this.setState({gameId: this.joinGameId.current.value, color: data.color, playerName: this.joinPlayerName.current.value})
            }
            else{
                this.setState({showInvalid:true})
            }  
        })
    }

    hide = () => {
        this.setState({showInvalid: false})
    }


    render () {
        return(
            <div>

                <NavBar />

                <div className='playWithFriendsBackground' style={{backgroundImage: 'url('+chess+')'}}>
                    <div className='playWithFriendsBox'>
                        {
                            this.state.gameId ? <Redirect to={{
                                pathname: '/waitingroom',
                                state: { 
                                    gameId: this.state.gameId,
                                    color: this.state.color,
                                    playerName: this.state.playerName
                                }
                            }} /> : null
                        }
                        <Tabs defaultActiveKey="create" className="tabs">
                            <Tab eventKey="create" title="CREATE GAME" className="tab">
                                <div style={{top:'10vh',position:'relative'}}>
                                    <Form.Group as={Row}>
                                        <Col sm="2"></Col>
                                        <Col sm="8">
                                            <Form.Control ref={this.createPlayerName} type="text" placeholder="Enter Player Name" />
                                        </Col>
                                        <Col sm="2"></Col>
                                    </Form.Group>
                                    <Form.Group as={Row}>
                                        <Col sm="4"></Col>
                                        <Col sm="4">
                                            <Button className="button" style={{backgroundColor:'black', border: '1px solid black'}} onClick={this.getGameId}>CREATE GAME</Button>
                                        </Col>
                                        <Col sm="4"></Col>
                                    </Form.Group>
                                </div>
                            </Tab>
                            <Tab eventKey="join" title="JOIN GAME" className="tab">
                                <div style={{top:'5vh',position:'relative'}}>
                                    <Form.Group as={Row}>
                                        <Col sm="2"></Col>
                                        <Col sm="8">
                                            <Form.Control ref={this.joinPlayerName} type="text" placeholder="Enter Player Name" />
                                        </Col>
                                        <Col sm="2"></Col>
                                    </Form.Group>
                                    <Form.Group as={Row}>
                                        <Col sm="2"></Col>
                                        <Col sm="8">
                                            <Form.Control ref={this.joinGameId} type="text" placeholder="Enter Game ID" />
                                        </Col>
                                        <Col sm="2"></Col>
                                    </Form.Group>
                                    <Form.Group as={Row}>
                                        <Col sm="4"></Col>
                                        <Col sm="4">
                                            <Button className="button" style={{backgroundColor:'black', border: '1px solid black'}} onClick={this.joinGame}>JOIN GAME</Button>
                                        </Col>
                                        <Col sm="4"></Col>
                                    </Form.Group>
                                </div>
                            </Tab>
                        </Tabs>
                    </div>
                </div>

                <Modal backdrop="static"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                    keyboard={false}
                    show={this.state.showInvalid}
                    onHide={this.hide}
                    className="my-modal"
                >
                    <Modal.Header closeButton hidden="true"></Modal.Header>
                    <Modal.Body>
                        <div style={{textAlign:"center"}}>
                            <h4>Invalid GAME ID</h4>
                            <Button onClick={this.hide} className='button' style={{backgroundColor:'black', border: '1px solid black',width: '150px',marginTop: '20px'}}>CLOSE</Button>
                        </div>
                    </Modal.Body>
                </Modal>
            </div>
        )
    }
}

export default PlayWithFriends
