import React, { Component } from 'react'
import { Container, Row, Modal, Spinner, Button } from 'react-bootstrap'
import UserNavBar from './UserNavBar'
import chess from './chess1.jpg'
import { connect } from 'react-redux';

class Home extends Component {
    state = {
        gameId: null,
        waiting: false,
        color: null
    }

    playOnline = () => {
        this.setState({ waiting: true });
        console.log(this.props.user)
        this.socket = new WebSocket("ws://localhost:8080/online/" + encodeURIComponent(this.props.user.email)) //CHANGE before commit
        this.socket.onopen = () => {
            console.log("socket connected");
        }

        this.socket.onclose = () => {
            console.log('socket closed')
        }

        this.socket.onmessage = (input) => {
            var data = JSON.parse(input.data);
            if (data.color) {
                this.setState({ gameId: data.gameId, color: data.color })
            }
        }

        this.props.setWs({ socket: this.socket });
    }

    hide = () => {
        this.setState({ waiting: false })
        console.log('socket closed')
        this.socket.close()
    }

    render() {
        return (
            <div>
                <UserNavBar />
                <div className='leftHalf grow' onClick={this.playOnline} >
                    <div className='imageBox'>
                        PLAY ONLINE
                        </div>
                    <div className='imageBox-text'>
                        Compete against strangers online
                    </div>
                </div>


                <div className='rightHalf grow '>
                    <div className='imageBox'>
                        PLAY WITH FRIENDS
                    </div>
                    <div className='imageBox-text'>
                        Create or join a room and compete against your friend
                    </div>
                </div>
                <Modal backdrop="static"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                    keyboard={false}
                    show={this.state.waiting}
                    onHide={this.hide}
                    className="my-modal"
                >
                    <Modal.Header closeButton hidden="true"></Modal.Header>
                    <Modal.Body>
                        <div style={{ textAlign: "center" }}>
                            <h4 style={{ marginBottom: '20px' }}>Searching for Opponent</h4>
                            <div>
                                <Spinner animation="border" variant="dark" />
                            </div>
                            <Button onClick={this.hide} className='button' style={{ backgroundColor: 'black', border: '1px solid black', width: '150px', marginTop: '20px' }}>CANCEL</Button>
                        </div>
                    </Modal.Body>
                </Modal>



            </div>

        )
    }
}

const mapStateToProps = (state) => ({
    user: state.userReducer
})

const mapDispatchToProps = (dispatch) => ({
    setWs: (data) => {
        const action = {
            type: 'WS',
            payload: data
        }
        return dispatch(action)
    }
})

export default connect(mapStateToProps, mapDispatchToProps)(Home)
