import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom'
import {get} from './RestService'

class OAuth2RedirectHandler extends Component {

    getToken(){
        const query = new URLSearchParams(this.props.location.search);
        const token = query.get('token')
        return token
    }

    render() {
        const token = this.getToken();
        if(token){
            fetch('http://localhost:8000/validate?gameId='+this.joinGameId.current.value+'&userId='+this.joinPlayerName.current.value)
                .then((response) => {
                    return response.json()
                })
                .then((data) => {
                    this.props.setUser({token: token,username:data.username,imageUrl:data.imageUrl});
                })

            return (
                <Redirect to={{
                    pathname: '/play',
                }} />
            )
        }

    }
}

const mapDispatchToProps = (dispatch) => ({
    setUser: (data) => {
        const action = {
            type: 'SET_USER',
            payload: data
        }
        return dispatch(action)
    } 
})

export default connect(null, mapDispatchToProps)(OAuth2RedirectHandler)
