import React, {Component} from 'react'
import { Redirect } from 'react-router-dom'
import {Button, Modal, Spinner} from 'react-bootstrap'
import NavBar from './NavBar'
import chess from './chess1.jpg'

class Play extends Component{
    state = {
        gameId : null,
        waiting : false,
        color : null,
        userId : null,
        playWithFriends : false,
        ws: null
    }

    playWithFriends = () => {
        this.setState({playWithFriends : true})
    }

    getDate = () => {
        const userId = Math.floor((new Date()).getTime() / 1000)
        this.setState({userId : userId})
        return userId
    }

    hide = () => {
        this.setState({waiting: false})
        console.log('socket closed')
        this.socket.close()
    }

    search = () => {
        this.setState({waiting: true});
        this.socket = new WebSocket("ws://localhost:8080/online/"+this.getDate()) //CHANGE before commit
        this.socket.onopen = () => {
            console.log("socket connected");
        }

        this.socket.onclose = () => {
            console.log('socket closed')
        }

        this.socket.onmessage = (input) => {
            var data = JSON.parse(input.data);
            if(data.color){
                this.setState({gameId: data.gameId, color: data.color})
            }
        }

        this.setState({ws: this.socket});
    }

    componentWillUnmount() {
        if (this.socket && this.socket.readyState === WebSocket.OPEN) {
            console.log("socket closed")
            this.socket.close();
         }
    }

    render(){
        const redirect = this.state.gameId ? <Redirect to={{
            pathname: '/game',
            state: { 
                gameId: this.state.gameId,
                color: this.state.color,
                userId: this.state.userId
            }
        }} /> :null

        return(
            <div>
                {redirect}

                <NavBar />

                <div style={{
                    height:'93vh',
                    backgroundImage: 'url('+chess+')',
                    backgroundSize: 'contain',
                    textAlign: 'center'
                }}
                >
                    <div className='homePage'>
                        <Button className='button' style={{backgroundColor: 'grey',border: '1px solid black',marginBottom: '10vh',width: '250px'}} onClick={this.search}>PLAY ONLINE</Button>
                        <Button className='button' style={{backgroundColor: 'grey',border: '1px solid black',width: '250px'}} onClick={this.playWithFriends}>PLAY WITH FRIENDS</Button>
                        {
                            this.state.playWithFriends ? <Redirect to="/playWithFriends" /> : null
                        }
                    </div>
                </div>
                <Modal backdrop="static"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                    keyboard={false}
                    show={this.state.waiting}
                    onHide={this.hide}
                    className="my-modal"
                >
                    <Modal.Header closeButton hidden="true"></Modal.Header>
                    <Modal.Body>
                        <div style={{textAlign:"center"}}>
                            <h4 style={{marginBottom:'20px'}}>Searching for Opponent</h4>
                            <div>
                                <Spinner animation="border" variant="dark" />
                            </div>
                            <Button onClick={this.hide} className='button' style={{backgroundColor:'black', border: '1px solid black',width: '150px',marginTop: '20px'}}>CANCEL</Button>
                        </div>
                    </Modal.Body>
                </Modal>            
            </div>            
        )
    }
}

export default Play
