import {Navbar, Nav} from 'react-bootstrap'
import queen from './images/white_queen.png';
import React, {Component} from 'react'
import {Modal, Button} from 'react-bootstrap'
import { Redirect } from 'react-router-dom';

class NavBar extends Component{

    state = {
        showModal: false,
        redirect: false 
    }

    hide = () => {
        this.setState({showModal : false})
    }

    handleClick = () => {
        this.setState({showModal : true})
    }

    redirect = () => {
        this.setState({redirect : true})
    }

    render(){
        return (
            <div>
                {this.state.redirect ? <Redirect to="/" /> : null}
        <Navbar className="navbar">
            <Navbar.Brand href="/">
                <img 
                    src={queen}
                    alt=""
                    className="d-inline-block align-top"
                    width="35"
                    height="30"
                    style={{marginRight:5}}
                />
                <span style={{
                    fontWeight : 'bold',
                    color : 'white'
                }}>
                    Chess Online
                </span>
            </Navbar.Brand>
            {
                this.props.showQuit ? 

            <Navbar.Collapse>
                <Nav className="ml-auto">
                    <Nav.Link  onClick={()=> this.handleClick()}>
                    <span style={{
                    fontWeight : 'bold',
                    color : 'white'
                }}>QUIT GAME</span>
                </Nav.Link>
                </Nav>
            </Navbar.Collapse>
            : null
            }
        </Navbar>
        <Modal backdrop="static"
                aria-labelledby="contained-modal-title-vcenter"
                centered
                keyboard={false}
                show={this.state.showModal}
                onHide={this.hide}
                className="my-modal"
                size="lg"
            >
                <Modal.Header closeButton hidden="true"></Modal.Header>
                <Modal.Body>
                    <div style={{textAlign:"center"}}>
                    <h4>Are you sure you want to quit the game?</h4>
                    <Button className="button" onClick={this.redirect} style={{marginRight: '40px',backgroundColor: 'black',border: '1px solid black',width: '150px',marginTop: '20px'}}>Yes</Button>
                    <Button className="button" onClick={this.hide} style={{backgroundColor: 'black',border: '1px solid black',width: '150px',marginTop: '20px'}}>No</Button>
                    </div>
                </Modal.Body>
            </Modal> 
        </div>
        )

    }
}

export default NavBar
